DROP TABLE IF EXISTS `sys_dicinfo`;CREATE TABLE `sys_dicinfo`  (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',  `dic_name` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '名称',  `dic_code` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '编码',  `dic_desc` varchar(1024) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '描述',  `parent_id` int(11) NULL DEFAULT NULL COMMENT '父类编号',  `sort_no` int(11) NOT NULL COMMENT '排序',  `in_use` int(1) NULL DEFAULT 1 COMMENT '是否可用，1可用，0不可用',  `create_time` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0),  `deleted` int(1) NULL DEFAULT 0 COMMENT '删除辨识  0 未删除 1 删除',  PRIMARY KEY (`id`) USING BTREE) ENGINE = InnoDB AUTO_INCREMENT = 428 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '字典表' ROW_FORMAT = Dynamic;
