DROP TABLE IF EXISTS `sys_resource`;CREATE TABLE `sys_resource`  (  `ID` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,  `NAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  `URL` varchar(128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  `IS_SHOW` decimal(1, 0) NULL DEFAULT 1 COMMENT '是否显示',  `PARENT_ID` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1',  `TREE_LEVEL` decimal(6, 0) NULL DEFAULT NULL,  `SORT` decimal(10, 0) NULL DEFAULT NULL,  `REMARK` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,  `CREATE_USER` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  `CREATE_TIME` datetime(6) NULL DEFAULT NULL,  `UPDATE_USER` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  `UPDATE_TIME` datetime(6) NULL DEFAULT NULL,  `DELETED` decimal(1, 0) NULL DEFAULT NULL,  `ICON_CLASS` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  `type` int(1) NULL DEFAULT 0,  `btn_type` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,  PRIMARY KEY (`ID`) USING BTREE,  INDEX `parentId`(`PARENT_ID`) USING BTREE) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;
